package InterviewProgarms;

public class EnglishUpperandLowerletterPrinting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Alphabates in Uppercase Are:");
		for(char ch='A'; ch<='Z';ch++)
		{
			System.out.print(ch +" ");
		}
		System.out.println("\n");
		System.out.println("Alphabates in Lower Are:");
		for(char ch='a'; ch<='z';ch++)
		{
			System.out.print(ch +" ");
		}
	}

}
